PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `fetch_logs` (
	`id` text PRIMARY KEY NOT NULL,
	`source_name` text NOT NULL,
	`status` text NOT NULL,
	`items_count` integer DEFAULT 0,
	`error_msg` text,
	`duration` integer NOT NULL,
	`created_at` integer
);
CREATE TABLE `posts` (
	`id` text PRIMARY KEY NOT NULL,
	`source_id` text NOT NULL,
	`external_id` text NOT NULL,
	"title_en" text,
	`summary_en` text,
	`source_url` text NOT NULL,
	`source_domain` text NOT NULL,
	`thumbnail_url` text,
	`author` text,
	`author_url` text,
	`score` integer DEFAULT 0,
	`translations` text DEFAULT '{}',
	`is_translated` integer DEFAULT false,
	`translated_at` integer,
	`tags` text DEFAULT '[]',
	`published_at` integer NOT NULL,
	`created_at` integer,
	`updated_at` integer, `title_original` text, `summary_original` text, `original_lang` text DEFAULT 'en', `origin_url` text,
	FOREIGN KEY (`source_id`) REFERENCES `sources`(`id`) ON UPDATE no action ON DELETE cascade
);
CREATE TABLE `sources` (
	`id` text PRIMARY KEY NOT NULL,
	`name` text NOT NULL,
	`display_name` text NOT NULL,
	`description` text,
	`api_endpoint` text,
	`min_score` integer DEFAULT 100,
	`max_posts` integer DEFAULT 300,
	`is_active` integer DEFAULT true,
	`created_at` integer,
	`updated_at` integer
);
CREATE TABLE `translation_cache` (
	`id` text PRIMARY KEY NOT NULL,
	`text_hash` text NOT NULL,
	`target_lang` text NOT NULL,
	`original_text` text NOT NULL,
	`translated_text` text NOT NULL,
	`created_at` integer
);
CREATE INDEX `logs_source_created_idx` ON `fetch_logs` (`source_name`,`created_at`);
CREATE UNIQUE INDEX `posts_source_external_idx` ON `posts` (`source_id`,`external_id`);
CREATE INDEX `posts_source_published_idx` ON `posts` (`source_id`,`published_at`);
CREATE INDEX `posts_score_idx` ON `posts` (`score`);
CREATE INDEX `posts_translated_idx` ON `posts` (`is_translated`);
CREATE UNIQUE INDEX `sources_name_unique` ON `sources` (`name`);
CREATE UNIQUE INDEX `cache_hash_lang_idx` ON `translation_cache` (`text_hash`,`target_lang`);
COMMIT;
